mapper.update:
update user_form_record
SET form_status= #{formStatus}, update_time=now(), form_desc =#{desc}
WHERE TO_DAYS(NOW()) - TO_DAYS(create_time) > #{day}
AND form_status =#{oldStatus}

mapper.select:
select uf.user_id,uf.form_id,ud.nickname,wa.open_id from (select user_id,MAX(form_id) as form_id from user_form_record where form_status='1' and create_time > #{expireTime} and user_id in
#{item}
group by user_id ) as uf left join user_detail as ud on uf.user_id = ud.user_id left join wechat_auth as wa on uf.user_id = wa.user_id
